import React, {Component} from 'react';


class Form extends Component {

    state = {
        answers: {
            answer1: "",
            answer2: "",
            answer3: "",
            answer4: "",
            answer5: "",
        },
        attempt: 1
    }

    onChangeHandler = (event) => {
        this.setState({
            answers: {
                [event.target.name]: event.target.value
            }
        })
    }

    submitHandler = () => {
        //send to API team name, number task, answers, attemp number
        console.log("submit");
        const newState = this.state;
        this.setState({
            ...newState,
            attempt: newState.attempt + 1
        })
    }

    render() {
        console.log("state", this.state);
        return (
            <div className="Form">
                <form onSubmit={this.submitHandler.bind(this)}>
                    <input
                        id="answer1"
                        type="text"
                        maxLength="20"
                        name="answer1"
                        value={this.state.answer1}
                        onChange={this.onChangeHandler.bind(this)}
                    />
                    <label htmlFor="answer1">1.</label>
                    <input
                        id="answer2"
                        type="text"
                        maxLength="20"
                        name="answer2"
                        value={this.state.answer2}
                        onChange={this.onChangeHandler.bind(this)}
                    />
                    <label htmlFor="answer1">2.</label>
                    <input
                        id="answer3"
                        type="text"
                        maxLength="20"
                        name="answer3"
                        value={this.state.answer3}
                        onChange={this.onChangeHandler.bind(this)}
                    />
                    <label htmlFor="answer1">3.</label>
                    <input
                        id="answer4"
                        type="text"
                        maxLength="20"
                        name="answer4"
                        value={this.state.answer4}
                        onChange={this.onChangeHandler.bind(this)}
                    />
                    <label htmlFor="answer1">4.</label>
                    <input
                        id="answer5"
                        type="text"
                        maxLength="20"
                        name="answer5"
                        value={this.state.answer5}
                        onChange={this.onChangeHandler.bind(this)}
                    />
                    <label htmlFor="answer1">5.</label>
                    <button type="submit" disabled={this.state.attempt > 3 || this.state.answers.length >= 3}>
                        Отправить ответы
                    </button>
                </form>
            </div>
        )
    }
}

export default Form;
