import React, {Component} from 'react';
import { NavLink } from 'react-router-dom';

class Home extends Component {
    state = {
        authorized: false
    }

    componentDidMount(){
        let check = localStorage.getItem('team');
        if (check) {
            this.setState({
                authorized: check
            });
        }
        console.log(this.props.history.location)
    }

    render() {
        return (
            <div>
                {!this.state.authorized ?
                    <div>
                        <NavLink to="/aaa">Team 1</NavLink>
                        <br/>
                        <NavLink to="/bbb">Team 2</NavLink>
                        <br/>
                        <NavLink to="/ccc">Team 3</NavLink>
                    </div>
                    :
                    <div>
                        Вы уже начали игру как команда {this.state.authorized}. Нажмите вперед в браузере.
                    </div>
                }
            </div> 
        )
    }
}

export default Home;
